import React, { useEffect, useState } from 'react'
import "./home.scss";
import { v4 as uuid } from 'uuid';
import EditModal from '../editModal/EditModal';
import DeleteModal from '../deleteModel/deleteModal';

const Home = () => {
    const unique_id = uuid();
    const [jobName, setJobName] = useState(null)
    const [jobPriotary, setJobPriotary] = useState(null);
    const [jobList, setJobList] = useState([])
    const [modalOpen, setModalOpen] = useState(false);
    const [modalDeleteOpen, setModalDeleteOpen] = useState(false);
    const [editData, setEditData] = useState()

    useEffect(() => {
        setJobList(JSON.parse(localStorage.getItem('jobList') || "[]"))
    }, [])

    const handleAddJob = () => {
        if (jobName != null && jobPriotary != null) {
            var jobList = JSON.parse(localStorage.getItem('jobList') || "[]");
            var job = {
                uuid: unique_id,
                jobName: jobName,
                jobPriotary: jobPriotary,
            };
            jobList.push(job);
            localStorage.setItem("jobList", JSON.stringify(jobList));
            setJobList(JSON.parse(localStorage.getItem('jobList') || "[]"))
        } else {
            console.log("null")
        }
    }
    const handleEditJob = (uuid) => {

        let items = JSON.parse(localStorage.getItem('jobList'));
        for (var i = 0; i < items.length; i++) {
            if (items[i].uuid === uuid) {
                console.log(items[i])
                setEditData(items[i])
                setModalOpen(true);
            }
        }

    }
    const handleDeleteJob = (uuid) => {

        let items = JSON.parse(localStorage.getItem('jobList'));
        for (var i = 0; i < items.length; i++) {
            if (items[i].uuid === uuid) {
                console.log(items[i])
                setEditData(items[i])
                setModalDeleteOpen(true)
            }
        }
    }
    const handleSearchJobName = () => {
        var input = document.getElementById("search_input");
        var filter = input.value.toUpperCase();
        var table = document.getElementById("jobTable");
        var tr = table.getElementsByTagName("tr");
        var td, i;

        for (i = 0; i < tr.length; i++) {
            td = tr[i].getElementsByTagName("td")[0];
            if (td) {
                if (td.innerHTML.toUpperCase().indexOf(filter) > -1) {
                    tr[i].style.display = "";
                } else {
                    tr[i].style.display = "none";
                }
            }
        }
    }
    const handleSearchJobPriotary = () => {
        data = data.filter((item) => item.state == 'New York').map(({ id, name, city }) => ({ id, name, city }));
    }
    return (
        <div className='container'>
            <div className="header">
                <h3>LOGO</h3>
            </div>
            <div className="content">
                <div className="create__content">
                    <div className="title">
                        <h5>Create New Job</h5>
                    </div>
                    <div className="create__content-items">
                        <div className="create__content-item">
                            <label htmlFor="">Job Name</label>
                            <input type="text" onChange={(e) => setJobName(e.target.value)} />
                        </div>
                        <div className="create__content-item">
                            <label htmlFor="">Job Priotary</label>
                            <select id="" placeholder='Priority(All)' onChange={(e) => setJobPriotary(e.target.value)}>
                                <option value="1">1</option>
                                <option value="2">2</option>
                                <option value="3">3</option>
                            </select>
                        </div>
                        <button onClick={() => handleAddJob()}>Create</button>
                    </div>
                    <div className="list__content-items">
                        <div className="title">
                            <h5>Create New Job</h5>
                        </div>
                        <div className='list__content-item'>
                            <input type="text" placeholder='Job Name' id='search_input' onKeyUp={handleSearchJobName} />
                            <select id="" placeholder='Priority(All)' onChange={handleSearchJobPriotary}>
                                <option value="1">1</option>
                                <option value="2">2</option>
                                <option value="3">3</option>
                            </select>
                        </div>
                        <div className="list__content-table">
                            <table id='jobTable'>
                                <thead>
                                    <tr>
                                        <th>Name</th>
                                        <th>Prioriy</th>
                                        <th>Action</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {jobList.sort((a, b) => a.jobPriotary > b.jobPriotary ? 1 : -1).map((job, index) => {
                                        return (
                                            <tr key={index}>
                                                <td>{job.jobName}</td>
                                                <td>
                                                    {job.jobPriotary == 1 && <button className=''>Acil</button>}
                                                    {job.jobPriotary == 2 && <button>Orta</button>}
                                                    {job.jobPriotary == 3 && <button>Önemsiz</button>}
                                                </td>
                                                <td><button onClick={() => handleEditJob(job.uuid)}>Düzenle</button> <button onClick={() => handleDeleteJob(job.uuid)}>Sil</button></td>
                                            </tr>
                                        )
                                    })}
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </div>
            {modalOpen && <EditModal setOpenModal={setModalOpen} editData={editData} />}
            {modalDeleteOpen && <DeleteModal setOpenDeleteModal={setModalDeleteOpen} editData={editData} />}

        </div>
    )
}

export default Home
