import React, { useEffect, useState } from 'react'
import "./home.scss";
import { v4 as uuid } from 'uuid';

const Home = () => {
    const unique_id = uuid();
    const [jobName, setJobName] = useState(null)
    const [jobPriotary, setJobPriotary] = useState(null);
    const [jobList, setJobList] = useState([])

    useEffect(() => {
        setJobList(JSON.parse(localStorage.getItem('jobList') || "[]"))
    }, [])

    const handleAddJob = () => {
        if (jobName != null && jobPriotary != null) {
            var jobList = JSON.parse(localStorage.getItem('jobList') || "[]");
            var job = {
                uuid: unique_id,
                jobName: jobName,
                jobPriotary: jobPriotary,
            };
            jobList.push(job);
            localStorage.setItem("jobList", JSON.stringify(jobList));
            console.log(JSON.parse(localStorage.getItem('jobList') || "[]"))
        } else {
            console.log("null")
        }
        // function addNewShow(titleArg, typeArg, genreArg, watchedArg) {
        //     // Get array from local storage, defaulting to empty array if it's not yet set
        //    
        //   };
    }
    return (
        <div className='container'>
            <div className="header">
                <h3>LOGO</h3>
            </div>
            <div className="content">
                <div className="create__content">
                    <div className="title">
                        <h5>Create New Job</h5>
                    </div>
                    <div className="create__content-items">
                        <div className="create__content-item">
                            <label htmlFor="">Job Name</label>
                            <input type="text" onChange={(e) => setJobName(e.target.value)} />
                        </div>
                        <div className="create__content-item">
                            <label htmlFor="">Job Priotary</label>
                            <select id="" placeholder='Priority(All)' onChange={(e) => setJobPriotary(e.target.value)}>
                                <option value="1">1</option>
                                <option value="2">2</option>
                                <option value="3">3</option>
                            </select>
                        </div>
                        <button onClick={() => handleAddJob()}>Create</button>
                    </div>
                    <div className="list__content-items">
                        <div className="title">
                            <h5>Create New Job</h5>
                        </div>
                        <div className='list__content-item'>
                            <input type="text" placeholder='Job Name' />
                            <select id="" placeholder='Priority(All)'></select>
                        </div>

                        <div className="list__content-table">
                            <table>
                                <thead>
                                    <tr>
                                        <th>Name</th>
                                        <th>Prioriy</th>
                                        <th>Action</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {jobList.map((job, index) => {
                                       return(
                                        <tr key={index}>
                                        <td>{job.jobName}</td>
                                        <td>{job.jobPriotary}</td>
                                        <td><button>DÃ¼zenle</button> <button>Sil</button></td>
                                    </tr>
                                       )
                                    })}
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </div>
            <div className="footer">
                footer
            </div>

        </div>
    )
}

export default Home
